acterizacion,hp.fecha AS 'Plan de Cuidado',A.fecha AS Fecha_Alerta,hs.fecha_toma AS Fecha_Signos,DATEDIFF(hc.fecha_create, hc.fecha) as diferencia_dias_carac FROM person P LEFT JOIN hog_fam F ON P.vivipersona = F.id_fam  LEFT JOIN hog_geo G ON F.idpre = G.idgeo  LEFT JOIN hog_alert A ON P.idpeople = A.idpeople and A.fecha>='$fecha_inicio' LEFT JOIN hog_signos hs ON P.idpeople = hs.idpeople and hs.fecha_toma>='$fecha_inicio' LEFT JOIN (SELECT C.* FROM hog_carac C INNER JOIN ( SELECT idfam, MAX(fecha) AS max_fecha
